import React, { useState, useEffect } from 'react';
import { useAuth } from '../../hooks/useAuth';
import { 
    fetchFriends, 
    fetchPotentialFriends, 
    sendFriendRequest, 
    getFriendRequests,
    acceptFriendRequest
} from '../../services/api';
import FriendCard from '../Matching/FriendCard';
import './Dashboard.css';

const Dashboard = () => {
    const { user, loading: authLoading } = useAuth();
    const [friends, setFriends] = useState([]);
    const [potentialFriends, setPotentialFriends] = useState([]);
    const [friendRequests, setFriendRequests] = useState([]);
    const [activeTab, setActiveTab] = useState('friends');
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    // Fetch friends data
    useEffect(() => {
        let isMounted = true;

        const loadFriends = async () => {
            if (authLoading || !user) return;

            try {
                if (isMounted) setLoading(true);
                const data = await fetchFriends();
                if (isMounted) setFriends(data);
            } catch (err) {
                console.error('Error fetching friends:', err);
                if (isMounted) setError('Failed to load your friends. Please try again later.');
            } finally {
                if (isMounted) setLoading(false);
            }
        };

        loadFriends();

        // Cleanup function to prevent state updates after unmounting
        return () => {
            isMounted = false;
        };
    }, [user, authLoading]);

    // Fetch potential friends
    useEffect(() => {
        let isMounted = true;

        const loadPotentialFriends = async () => {
            if (authLoading || !user || activeTab !== 'discover') return;

            try {
                if (isMounted) setLoading(true);
                const data = await fetchPotentialFriends();
                if (isMounted) setPotentialFriends(data);
            } catch (err) {
                console.error('Error fetching potential friends:', err);
                if (isMounted) setError('Failed to load potential friends. Please try again later.');
            } finally {
                if (isMounted) setLoading(false);
            }
        };

        loadPotentialFriends();

        // Cleanup function to prevent state updates after unmounting
        return () => {
            isMounted = false;
        };
    }, [activeTab, user, authLoading]);

    // Fetch friend requests
    useEffect(() => {
        let isMounted = true;

        const loadFriendRequests = async () => {
            if (authLoading || !user || activeTab !== 'requests') return;

            try {
                if (isMounted) setLoading(true);
                const data = await getFriendRequests();
                if (isMounted) setFriendRequests(data);
            } catch (err) {
                console.error('Error fetching friend requests:', err);
                if (isMounted) setError('Failed to load friend requests. Please try again later.');
            } finally {
                if (isMounted) setLoading(false);
            }
        };

        loadFriendRequests();

        // Cleanup function to prevent state updates after unmounting
        return () => {
            isMounted = false;
        };
    }, [activeTab, user, authLoading]);

    // Handler for sending friend request
    const handleSendRequest = async (friendId) => {
        try {
            await sendFriendRequest(friendId);
            // Remove this person from potential friends after sending request
            setPotentialFriends(prev => prev.filter(friend => friend._id !== friendId));
        } catch (err) {
            console.error('Error sending friend request:', err);
            setError('Failed to send friend request. Please try again.');
        }
    };

    // Handler for accepting friend request
    const handleAcceptRequest = async (requestId) => {
        try {
            await acceptFriendRequest(requestId);
            // Remove this request from the list after accepting
            setFriendRequests(prev => prev.filter(req => req._id !== requestId));
            // Refresh friends list
            const updatedFriends = await fetchFriends();
            setFriends(updatedFriends);
        } catch (err) {
            console.error('Error accepting friend request:', err);
            setError('Failed to accept friend request. Please try again.');
        }
    };

    if (authLoading || !user) {
        return (
            <div className="loading-container">
                <div className="loading-spinner"></div>
                <p>Loading your profile...</p>
            </div>
        );
    }

    return (
        <div className="dashboard">
            <header className="dashboard-header">
                <h1>Welcome, {user.username || user.email || 'Friend'}!</h1>
                <div className="profile-summary">
                    <img 
                        src={user.profilePicture || 'https://via.placeholder.com/50'} 
                        alt="Profile" 
                        className="profile-pic"
                        onClick={() => window.location.href = '/profile'}
                        style={{ cursor: 'pointer' }}
                    />
                    <a href="/profile" className="edit-profile-link">Edit Profile</a>
                </div>
            </header>

            <nav className="dashboard-nav">
                <button 
                    className={activeTab === 'friends' ? 'active' : ''} 
                    onClick={() => setActiveTab('friends')}
                >
                    My Friends
                </button>
                <button 
                    className={activeTab === 'discover' ? 'active' : ''} 
                    onClick={() => setActiveTab('discover')}
                >
                    Discover
                </button>
                <button 
                    className={activeTab === 'requests' ? 'active' : ''} 
                    onClick={() => setActiveTab('requests')}
                >
                    Requests
                </button>
            </nav>

            {error && <div className="error-message">{error}</div>}

            {loading ? (
                <div className="loading-container">
                    <div className="loading-spinner"></div>
                    <p>Loading...</p>
                </div>
            ) : (
                <div className="dashboard-content">
                    {activeTab === 'friends' && (
                        <>
                            <h2>Your Friends</h2>
                            <div className="friend-list">
                                {friends && friends.length > 0 ? (
                                    friends.map(friend => (
                                        <FriendCard 
                                            key={friend._id} 
                                            friend={friend}
                                            actionLabel="Message"
                                            onAction={() => {/* Navigate to chat */}}
                                        />
                                    ))
                                ) : (
                                    <div className="empty-state">
                                        <p>You haven't connected with any friends yet!</p>
                                        <button onClick={() => setActiveTab('discover')}>
                                            Find Friends
                                        </button>
                                    </div>
                                )}
                            </div>
                        </>
                    )}

                    {activeTab === 'discover' && (
                        <>
                            <h2>People You May Know</h2>
                            <div className="friend-list">
                                {potentialFriends && potentialFriends.length > 0 ? (
                                    potentialFriends.map(friend => (
                                        <FriendCard 
                                            key={friend._id} 
                                            friend={friend}
                                            actionLabel="Connect"
                                            onAction={() => handleSendRequest(friend._id)}
                                        />
                                    ))
                                ) : (
                                    <div className="empty-state">
                                        <p>No new people to discover right now.</p>
                                        <p>Check back later!</p>
                                    </div>
                                )}
                            </div>
                        </>
                    )}

                    {activeTab === 'requests' && (
                        <>
                            <h2>Friend Requests</h2>
                            <div className="friend-list">
                                {friendRequests && friendRequests.length > 0 ? (
                                    friendRequests.map(request => (
                                        <FriendCard 
                                            key={request._id} 
                                            friend={request.from}
                                            actionLabel="Accept"
                                            onAction={() => handleAcceptRequest(request._id)}
                                        />
                                    ))
                                ) : (
                                    <div className="empty-state">
                                        <p>No pending friend requests.</p>
                                    </div>
                                )}
                            </div>
                        </>
                    )}
                </div>
            )}
        </div>
    );
};

export default Dashboard;
