import React, { useEffect, useState, useCallback, useRef } from 'react';
import { useAuth } from '../../hooks/useAuth';
import { useParams, useHistory } from 'react-router-dom';
import { 
    sendMessage, 
    receiveMessages, 
    connectSocket, 
    disconnectSocket, 
    markMessagesAsRead,
    onMessagesRead,
    onMessageSent,
    onMessageError,
    sendTypingIndicator,
    onUserTyping,
    getSocket
} from '../../services/socket';
import { getChatHistory, markMessagesAsRead as apiMarkMessagesAsRead } from '../../services/api';
import '../../styles/chat.css';

const ChatInterface = () => {
    const { user } = useAuth();
    const { friendId } = useParams();
    const history = useHistory();
    const [friend, setFriend] = useState(null);
    const [messages, setMessages] = useState([]);
    const [newMessage, setNewMessage] = useState('');
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [isTyping, setIsTyping] = useState(false);
    const [typingTimeout, setTypingTimeout] = useState(null);
    const messagesEndRef = useRef(null);
    const socket = io(process.env.REACT_APP_SOCKET_URL || 'http://localhost:5000');

    // Setup socket connection and handlers
    useEffect(() => {
        // Connect to socket when component mounts
        connectSocket();

        const handleReceiveMessage = (message) => {
            setMessages((prevMessages) => [...prevMessages, message]);

            // If we receive a message from the friend we're chatting with, mark it as read
            if (message.sender === friendId && user?.id) {
                markMessagesAsRead({ userId: user.id, friendId });
            }
        };

        const handleMessageSent = (confirmation) => {
            // Update the message in our state with the server-generated ID
            setMessages((prevMessages) => 
                prevMessages.map(msg => 
                    (msg.isSent && !msg._id && msg.timestamp.getTime() === new Date(confirmation.timestamp).getTime())
                        ? { ...msg, _id: confirmation._id, timestamp: new Date(confirmation.timestamp) }
                        : msg
                )
            );
        };

        const handleMessageError = (error) => {
            setError(`Error sending message: ${error.message}`);
            setTimeout(() => setError(null), 5000);
        };

        const handleMessagesRead = (data) => {
            if (data.by === friendId) {
                // Update read status of messages in state
                setMessages((prevMessages) => 
                    prevMessages.map(msg => 
                        (msg.sender === user?.id && !msg.read)
                            ? { ...msg, read: true, readAt: new Date() }
                            : msg
                    )
                );
            }
        };

        const handleUserTyping = (data) => {
            if (data.sender === friendId) {
                setIsTyping(true);

                // Clear any existing timeout
                if (typingTimeout) {
                    clearTimeout(typingTimeout);
                }

                // Set a timeout to clear the typing indicator after 3 seconds
                const timeout = setTimeout(() => {
                    setIsTyping(false);
                }, 3000);

                setTypingTimeout(timeout);
            }
        };

        // Register socket event handlers
        receiveMessages(handleReceiveMessage);
        onMessageSent(handleMessageSent);
        onMessageError(handleMessageError);
        onMessagesRead(handleMessagesRead);
        onUserTyping(handleUserTyping);

        // Notify server that user is online if we have a user ID
        if (user && user.id) {
            socket.emit('user_login', user.id);
        }

        return () => {
            // Cleanup the socket connection when component unmounts
            disconnectSocket();

            // Clear any existing typing timeout to prevent memory leaks
            if (typingTimeout) {
                clearTimeout(typingTimeout);
            }
        };
    }, [user, friendId]);

    // Load friend details and chat history
    useEffect(() => {
        const loadFriendAndChat = async () => {
            if (!friendId || !user) return;

            setLoading(true);
            try {
                // Import this function at the top of the file
                const { getFriendById } = await import('../../services/api');

                // Get friend details
                const friendData = await getFriendById(friendId);
                setFriend(friendData);

                // Get chat history
                const chatHistory = await getChatHistory(friendId);

                // Format the timestamps as Date objects
                const formattedHistory = chatHistory.map(msg => ({
                    ...msg,
                    timestamp: new Date(msg.timestamp),
                    readAt: msg.readAt ? new Date(msg.readAt) : null
                }));

                setMessages(formattedHistory);

                // Mark messages as read when chat is opened
                if (user.id) {
                    markMessagesAsRead({ userId: user.id, friendId });
                    await apiMarkMessagesAsRead(friendId);
                }
            } catch (error) {
                console.error('Error loading chat data:', error);
                setError('Failed to load chat data. Please try again.');
                setTimeout(() => setError(null), 5000);
            } finally {
                setLoading(false);
            }
        };

        loadFriendAndChat();
    }, [friendId, user]);

    // Scroll to bottom of messages when they update
    useEffect(() => {
        const scrollToBottom = () => {
            const chatContainer = document.querySelector('.chat-messages');
            if (chatContainer) {
                chatContainer.scrollTop = chatContainer.scrollHeight;
            }
        };

        scrollToBottom();
    }, [messages]);

    const handleSendMessage = () => {
        if (newMessage.trim() && user && user.id && friendId) {
            const messageData = {
                sender: user.id,
                recipient: friendId,
                content: newMessage
            };

            // Send via socket
            sendMessage(messageData);

            // Add message to local state
            const newMsg = {
                _id: null, // Will be updated when server confirms
                sender: user.id,
                receiver: friendId,
                content: newMessage,
                timestamp: new Date(),
                read: false,
                readAt: null,
                isSent: true
            };

            setMessages((prevMessages) => [...prevMessages, newMsg]);
            setNewMessage('');

            // Scroll to bottom
            if (messagesEndRef.current) {
                messagesEndRef.current.scrollIntoView({ behavior: 'smooth' });
            }
        }
    };

    // Handle back button to return to dashboard
    const handleBack = () => {
        history.push('/dashboard');
    };

    if (loading) {
        return (
            <div className="loading-container">
                <div className="loading-spinner"></div>
                <p>Loading chat...</p>
            </div>
        );
    }

    return (
        <div className="chat-container">
            <div className="chat-header">
                <button className="back-button" onClick={handleBack}>
                    &larr; Back
                </button>

                {friend && (
                    <div className="chat-friend-info">
                        <img 
                            src={friend.profilePicture || 'https://via.placeholder.com/40'} 
                            alt={friend.username} 
                            className="chat-avatar"
                        />
                        <div className="chat-name">
                            <h3>{friend.username}</h3>
                            <p>{friend.location}</p>
                        </div>
                    </div>
                )}
            </div>

            <div className="chat-messages">
                {error && <div className="error-message">{error}</div>}

                {messages.length === 0 ? (
                    <div className="no-messages">
                        <p>No messages yet. Start the conversation!</p>
                    </div>
                ) : (
                    messages.map((msg, index) => (
                        <div 
                            key={index} 
                            className={`message ${msg.sender === user.id ? 'sent' : 'received'}`}
                        >
                            <div className="message-content">{msg.content}</div>
                            <div className="message-info">
                                <span className="message-time">
                                    {msg.timestamp.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}
                                </span>
                                {msg.sender === user.id && (
                                    <span className="message-status">
                                        {msg.read ? (
                                            <span className="status-read" title={`Read at ${msg.readAt ? msg.readAt.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' }) : ''}`}>
                                                ✓✓ Read
                                            </span>
                                        ) : (
                                            <span className="status-sent">
                                                ✓ Sent
                                            </span>
                                        )}
                                    </span>
                                )}
                            </div>
                        </div>
                    ))
                )}
                {isTyping && (
                    <div className="typing-indicator">
                        <span>{friend?.username || 'Friend'} is typing</span>
                    </div>
                )}
                <div ref={messagesEndRef} />
            </div>

            <div className="chat-input">
                <input
                    type="text"
                    value={newMessage}
                    onChange={(e) => {
                        setNewMessage(e.target.value);
                        // Send typing indicator when user types
                        if (user?.id && friendId) {
                            sendTypingIndicator(user.id, friendId);
                        }
                    }}
                    placeholder="Type a message..."
                    onKeyPress={(e) => e.key === 'Enter' && handleSendMessage()}
                />
                <button onClick={handleSendMessage} disabled={!newMessage.trim()}>Send</button>
            </div>
        </div>
    );
};

export default ChatInterface;
