import React, { useState, useEffect } from 'react';
import { useHistory, useLocation } from 'react-router-dom';
import { useAuth } from '../../hooks/useAuth';
import { updateUserProfile, getUserProfile } from '../../services/api';
import ProfilePrompts from './ProfilePrompts';
import '../../styles/profile.css';

const Profile = () => {
    const { user, setUser, isNewUser, setIsNewUser } = useAuth();
    const history = useHistory();
    const location = useLocation();
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    const [success, setSuccess] = useState('');
    const [showQuestionnaire, setShowQuestionnaire] = useState(false);
    
    // Profile data
    const [username, setUsername] = useState('');
    const [email, setEmail] = useState('');
    const [bio, setBio] = useState('');
    const [userLocation, setUserLocation] = useState('');
    const [profilePicture, setProfilePicture] = useState('');
    
    // Interests
    const [interests, setInterests] = useState([]);
    const [newInterest, setNewInterest] = useState('');
    
    // Popular interests suggestions
    const popularInterests = [
        'Travel', 'Music', 'Movies', 'Reading', 'Cooking', 
        'Fitness', 'Gaming', 'Photography', 'Art', 'Sports',
        'Technology', 'Fashion', 'Hiking', 'Dance', 'Writing',
        'Yoga', 'Meditation', 'Cycling', 'Swimming', 'Running'
    ];
    
    // Check if user is new from query param
    useEffect(() => {
        const params = new URLSearchParams(routerLocation.search);
        const isNewUserParam = params.get('newUser') === 'true';
        
        if (isNewUserParam) {
            setShowQuestionnaire(true);
        }
    }, [routerLocation]);
    
    useEffect(() => {
        let isMounted = true;
        const loadUserProfile = async () => {
            if (!user) {
                // The PrivateRoute component will handle redirects now
                return;
            }
            
            try {
                setLoading(true);
                const profileData = await getUserProfile();
                
                if (isMounted) {
                    // Fill form with user data
                    setUsername(profileData.username || '');
                    setEmail(profileData.email || '');
                    setBio(profileData.bio || '');
                    setUserLocation(profileData.location || '');
                    setProfilePicture(profileData.profilePicture || '');
                    setInterests(profileData.interests || []);
                }
            } catch (err) {
                console.error('Error loading profile:', err);
                if (isMounted) {
                    setError('Failed to load your profile. Please try again.');
                }
            } finally {
                if (isMounted) {
                    setLoading(false);
                }
            }
        };
        
        loadUserProfile();
        
        // Cleanup function
        return () => {
            isMounted = false;
        };
    }, [user]);
    
    const handleAddInterest = (interest = newInterest) => {
        if (!interest.trim()) return;
        
        // Check if interest already exists (case insensitive)
        if (interests.some(i => i.toLowerCase() === interest.toLowerCase())) {
            return;
        }
        
        setInterests([...interests, interest.trim()]);
        setNewInterest('');
    };
    
    const handleRemoveInterest = (indexToRemove) => {
        setInterests(interests.filter((_, index) => index !== indexToRemove));
    };
    
    const handleSubmit = async (e) => {
        e.preventDefault();
        setError('');
        setSuccess('');
        
        try {
            setLoading(true);
            
            const profileData = {
                bio,
                location: userLocation,
                profilePicture,
                interests
            };
            
            await updateUserProfile(profileData);
            
            // Update local user state with new profile data
            if (setUser) {
                setUser(prev => ({
                    ...prev,
                    bio,
                    location: userLocation,
                    profilePicture,
                    interests
                }));
            }
            
            setSuccess('Profile updated successfully!');
            
            // Success message disappears after 3 seconds
            setTimeout(() => setSuccess(''), 3000);
        } catch (err) {
            console.error('Error updating profile:', err);
            setError('Failed to update your profile. Please try again.');
        } finally {
            setLoading(false);
        }
    };
    
    if (loading && !user) {
        return (
            <div className="loading-container">
                <div className="loading-spinner"></div>
                <p>Loading your profile...</p>
            </div>
        );
    }
    
    return (
        <div className="profile-page">
            {showQuestionnaire && (
                <div className="onboarding-prompt-overlay">
                    <div className="onboarding-prompt-container">
                        <h2>Welcome to Friend Finder!</h2>
                        <p>Let's get to know you better to find the best matches.</p>
                        <ProfilePrompts forNewUser={true} />
                    </div>
                </div>
            )}
            <div className="profile-container">
                <h2>Edit Your Profile</h2>
                
                {error && <div className="error-message">{error}</div>}
                {success && <div className="success-message">{success}</div>}
                
                <form onSubmit={handleSubmit}>
                    <div className="profile-section">
                        <div className="form-group">
                            <label>Username</label>
                            <input
                                type="text"
                                value={username}
                                disabled
                                className="disabled-input"
                            />
                            <small>Username cannot be changed</small>
                        </div>
                        
                        <div className="form-group">
                            <label>Email</label>
                            <input
                                type="email"
                                value={email}
                                disabled
                                className="disabled-input"
                            />
                            <small>Email cannot be changed</small>
                        </div>
                        
                        <div className="form-group">
                            <label>Bio</label>
                            <textarea
                                value={bio}
                                onChange={(e) => setBio(e.target.value)}
                                placeholder="Tell us about yourself..."
                                rows="4"
                            />
                        </div>
                        
                        <div className="form-group">
                            <label>Location</label>
                            <input
                                type="text"
                                value={userLocation}
                                onChange={(e) => setUserLocation(e.target.value)}
                                placeholder="Where are you from?"
                            />
                        </div>
                        
                        <div className="form-group">
                            <label>Profile Picture URL</label>
                            <input
                                type="url"
                                value={profilePicture}
                                onChange={(e) => setProfilePicture(e.target.value)}
                                placeholder="https://example.com/your-photo.jpg"
                            />
                            {profilePicture && (
                                <div className="profile-preview">
                                    <img
                                        src={profilePicture}
                                        alt="Profile preview"
                                        onError={(e) => {
                                            e.target.onerror = null;
                                            e.target.src = 'https://via.placeholder.com/150?text=Invalid+URL';
                                        }}
                                    />
                                </div>
                            )}
                            <small>Enter a URL to an image (JPG, PNG)</small>
                        </div>
                    </div>
                    
                    <div className="profile-section interests-section">
                        <h3>Your Interests</h3>
                        <p className="section-description">
                            Add interests to help find friends with similar passions
                        </p>
                        
                        <div className="interests-container">
                            {interests.map((interest, index) => (
                                <div key={index} className="interest-tag with-delete">
                                    {interest}
                                    <button
                                        type="button"
                                        className="remove-interest"
                                        onClick={() => handleRemoveInterest(index)}
                                    >
                                        ×
                                    </button>
                                </div>
                            ))}
                        </div>
                        
                        <div className="add-interest">
                            <input
                                type="text"
                                value={newInterest}
                                onChange={(e) => setNewInterest(e.target.value)}
                                placeholder="Add a new interest..."
                            />
                            <button
                                type="button"
                                onClick={() => handleAddInterest()}
                                disabled={!newInterest.trim()}
                            >
                                Add
                            </button>
                        </div>
                        
                        <div className="popular-interests">
                            <h4>Popular Interests</h4>
                            <div className="interests-container">
                                {popularInterests.map((interest, index) => (
                                    <div
                                        key={index}
                                        className={`interest-tag suggestion ${
                                            interests.some(i => i.toLowerCase() === interest.toLowerCase())
                                                ? 'selected'
                                                : ''
                                        }`}
                                        onClick={() => !interests.some(i => i.toLowerCase() === interest.toLowerCase()) && handleAddInterest(interest)}
                                    >
                                        {interest}
                                    </div>
                                ))}
                            </div>
                        </div>
                    </div>
                    
                    <div className="form-actions">
                        <button
                            type="submit"
                            className="save-button"
                            disabled={loading}
                        >
                            {loading ? 'Saving...' : 'Save Profile'}
                        </button>
                        <button
                            type="button"
                            className="cancel-button"
                            onClick={() => history.push('/dashboard')}
                        >
                            Cancel
                        </button>
                    </div>
                </form>
            </div>
        </div>
    );
};

export default Profile;
