import React, { useEffect, useState, useCallback, useRef } from 'react';
import { useAuth } from '../../hooks/useAuth';
import { useParams, useHistory } from 'react-router-dom';
import { 
    sendMessage, 
    receiveMessages, 
    connectSocket, 
    disconnectSocket, 
    markMessagesAsRead,
    onMessagesRead,
    onMessageSent,
    onMessageError
} from '../../services/socket';
import { getChatHistory, markMessagesAsRead as apiMarkMessagesAsRead } from '../../services/api';
import { io } from 'socket.io-client';
import '../../styles/chat.css';

const ChatInterface = () => {
    const { user } = useAuth();
    const { friendId } = useParams();
    const history = useHistory();
    const [friend, setFriend] = useState(null);
    const [messages, setMessages] = useState([]);
    const [newMessage, setNewMessage] = useState('');
    const [loading, setLoading] = useState(true);
    const socket = io(process.env.REACT_APP_SOCKET_URL || 'http://localhost:5000');

    useEffect(() => {
        // Connect to socket when component mounts
        connectSocket();

        const handleReceiveMessage = (message) => {
            setMessages((prevMessages) => [...prevMessages, message]);
        };

        receiveMessages(handleReceiveMessage);

        // Notify server that user is online if we have a user ID
        if (user && user.id) {
            socket.emit('user_login', user.id);
        }

        return () => {
            // Cleanup the socket connection when component unmounts
            disconnectSocket();
        };
    }, [user]);

    // Load friend details and chat history
    useEffect(() => {
        const loadFriendAndChat = async () => {
            if (!friendId || !user) return;

            setLoading(true);
            try {
                // Import these functions at the top of the file
                const { getFriendById, getChatHistory } = await import('../../services/api');

                // Get friend details
                const friendData = await getFriendById(friendId);
                setFriend(friendData);

                // Get chat history
                const chatHistory = await getChatHistory(friendId);
                setMessages(chatHistory);
            } catch (error) {
                console.error('Error loading chat data:', error);
                // Handle error - maybe show error message or redirect
            } finally {
                setLoading(false);
            }
        };

        loadFriendAndChat();
    }, [friendId, user]);

    // Scroll to bottom of messages when they update
    useEffect(() => {
        const scrollToBottom = () => {
            const chatContainer = document.querySelector('.chat-messages');
            if (chatContainer) {
                chatContainer.scrollTop = chatContainer.scrollHeight;
            }
        };

        scrollToBottom();
    }, [messages]);

    const handleSendMessage = () => {
        if (newMessage.trim() && user && user.id && friendId) {
            const messageData = {
                sender: user.id,
                recipient: friendId,
                content: newMessage
            };

            sendMessage(messageData);

            // Add message to local state
            setMessages((prevMessages) => [
                ...prevMessages, 
                {
                    sender: user.id,
                    receiver: friendId,
                    content: newMessage,
                    timestamp: new Date(),
                    isSent: true
                }
            ]);

            setNewMessage('');
        }
    };

    // Handle back button to return to dashboard
    const handleBack = () => {
        history.push('/dashboard');
    };

    if (loading) {
        return (
            <div className="loading-container">
                <div className="loading-spinner"></div>
                <p>Loading chat...</p>
            </div>
        );
    }

    return (
        <div className="chat-container">
            <div className="chat-header">
                <button className="back-button" onClick={handleBack}>
                    &larr; Back
                </button>

                {friend && (
                    <div className="chat-friend-info">
                        <img 
                            src={friend.profilePicture || 'https://via.placeholder.com/40'} 
                            alt={friend.username} 
                            className="chat-avatar"
                        />
                        <div className="chat-name">
                            <h3>{friend.username}</h3>
                            <p>{friend.location}</p>
                        </div>
                    </div>
                )}
            </div>

            <div className="chat-messages">
                {messages.length === 0 ? (
                    <div className="no-messages">
                        <p>No messages yet. Start the conversation!</p>
                    </div>
                ) : (
                    messages.map((msg, index) => (
                        <div 
                            key={index} 
                            className={`message ${msg.sender === user.id ? 'sent' : 'received'}`}
                        >
                            <div className="message-content">{msg.content}</div>
                            <div className="message-time">
                                {new Date(msg.timestamp).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}
                            </div>
                        </div>
                    ))
                )}
            </div>

            <div className="chat-input">
                <input
                    type="text"
                    value={newMessage}
                    onChange={(e) => setNewMessage(e.target.value)}
                    placeholder="Type a message..."
                    onKeyPress={(e) => e.key === 'Enter' && handleSendMessage()}
                />
                <button onClick={handleSendMessage} disabled={!newMessage.trim()}>Send</button>
            </div>
        </div>
    );
};

export default ChatInterface;
