/**
 * Helper utility to inspect behavior tracking data in development mode
 */

const inspectTrackingData = () => {
  if (process.env.NODE_ENV !== 'development') {
    console.warn('This function is meant to be used only in development mode');
    return null;
  }
  
  try {
    // Try to get data from localStorage
    const localData = localStorage.getItem('devUserTracking');
    if (!localData) {
      console.log('No tracking data found in localStorage');
      return null;
    }
    
    const data = JSON.parse(localData);
    
    // Display summary
    console.log('%c--- BEHAVIOR TRACKING DATA SUMMARY ---', 'font-size: 16px; font-weight: bold; color: #4CAF50;');
    console.log(`Total interactions tracked: ${data.interactions?.length || 0}`);
    console.log(`Last updated: ${data.lastUpdated}`);
    
    // Topic analysis from conversationTopics object
    console.log('\n%c--- CONVERSATION TOPICS ---', 'font-size: 14px; font-weight: bold; color: #2196F3;');
    if (data.conversationTopics && Object.keys(data.conversationTopics).length > 0) {
      // Sort topics by count
      const sortedTopics = Object.entries(data.conversationTopics)
        .sort((a, b) => b[1].count - a[1].count);
      
      sortedTopics.forEach(([topic, details]) => {
        if (details.count > 0) {
          console.log(`%c${topic}: ${details.count} mentions`, 'color: #2196F3; font-weight: bold;');
          
          if (details.examples && details.examples.length > 0) {
            console.log('  Examples:');
            details.examples.forEach((example, i) => {
              console.log(`    ${i+1}. "${example.snippet}" (${new Date(example.timestamp).toLocaleString()})`);
            });
          }
          
          if (details.lastDetected) {
            console.log(`  Last detected: ${new Date(details.lastDetected).toLocaleString()}`);
          }
          console.log(''); // empty line for spacing
        }
      });
    } else {
      console.log('No conversation topics detected yet.');
      
      // Fallback to manual analysis if conversationTopics is empty
      console.log('\n%c--- MANUAL TOPIC ANALYSIS (FALLBACK) ---', 'font-size: 14px; font-weight: bold; color: #FF9800;');
      
      if (data.interactions && data.interactions.length > 0) {
        const topics = {
          'music': 0,
          'movies': 0,
          'travel': 0,
          'food': 0,
          'tech': 0,
          'sports': 0,
          'education': 0,
          'work': 0,
          'art': 0,
          'health': 0
        };
        
        // Simple keyword matching to count topic mentions
        data.interactions.forEach(interaction => {
          if (interaction.message && interaction.message.content) {
            const content = interaction.message.content.toLowerCase();
            
            if (/music|song|band|concert|playlist|album/.test(content)) topics.music++;
            if (/movie|film|cinema|watch|actor|actress|director/.test(content)) topics.movies++;
            if (/travel|trip|vacation|visit|flight|hotel|destination/.test(content)) topics.travel++;
            if (/food|restaurant|eat|dinner|lunch|recipe|cook|meal/.test(content)) topics.food++;
            if (/tech|technology|computer|phone|app|software|device/.test(content)) topics.tech++;
            if (/sport|game|team|play|match|win|workout|exercise/.test(content)) topics.sports++;
            if (/learn|study|school|college|university|degree|class|course/.test(content)) topics.education++;
            if (/work|job|career|office|business|meeting|project|company/.test(content)) topics.work++;
            if (/art|painting|drawing|design|creative|artist|gallery|museum/.test(content)) topics.art++;
            if (/health|doctor|medical|fitness|wellness|diet|exercise|sleep/.test(content)) topics.health++;
          }
        });
        
        Object.entries(topics)
          .sort((a, b) => b[1] - a[1])
          .forEach(([topic, count]) => {
            if (count > 0) {
              console.log(`${topic}: ${count} mentions`);
            }
          });
      } else {
        console.log('No messages to analyze.');
      }
    }
    
    // Return the full data for further inspection
    return data;
  } catch (error) {
    console.error('Error inspecting tracking data:', error);
    return null;
  }
};

window.inspectTrackingData = inspectTrackingData;

console.log(
  '%c Behavior Tracking Debug Utilities Loaded ',
  'background: #222; color: #bada55; font-size: 16px;'
);
console.log(
  '%c Call window.inspectTrackingData() to analyze tracking data ',
  'background: #222; color: #bada55; font-size: 14px;'
);

export default inspectTrackingData;
