import React, { useEffect, useState } from 'react';
import { useAuth } from '../../hooks/useAuth';
import { sendMessage, receiveMessages, connectSocket, disconnectSocket } from '../../services/socket';
import { io } from 'socket.io-client';

const ChatInterface = () => {
    const { user } = useAuth();
    const [messages, setMessages] = useState([]);
    const [newMessage, setNewMessage] = useState('');
    const socket = io(process.env.REACT_APP_SOCKET_URL || 'http://localhost:5000');

    useEffect(() => {
        // Connect to socket when component mounts
        connectSocket();

        const handleReceiveMessage = (message) => {
            setMessages((prevMessages) => [...prevMessages, message]);
        };

        receiveMessages(handleReceiveMessage);

        // Notify server that user is online if we have a user ID
        if (user && user.id) {
            socket.emit('user_login', user.id);
        }

        return () => {
            // Cleanup the socket connection when component unmounts
            disconnectSocket();
        };
    }, [user]);

    const handleSendMessage = () => {
        if (newMessage.trim() && user && user.id) {
            // In a real app, you would get the recipient ID from props or state
            const recipientId = "recipientId"; // Replace with actual recipient ID

            const messageData = {
                sender: user.id,
                recipient: recipientId,
                content: newMessage
            };

            sendMessage(messageData);

            // Add message to local state
            setMessages((prevMessages) => [
                ...prevMessages, 
                {
                    sender: user.id,
                    content: newMessage,
                    timestamp: new Date(),
                    isSent: true
                }
            ]);

            setNewMessage('');
        }
    };

    return (
        <div className="chat-interface">
            <div className="messages">
                {messages.map((msg, index) => (
                    <div key={index} className={msg.sender === user.id ? 'message sent' : 'message received'}>
                        <span>{msg.content}</span>
                    </div>
                ))}
            </div>
            <input
                type="text"
                value={newMessage}
                onChange={(e) => setNewMessage(e.target.value)}
                placeholder="Type a message..."
            />
            <button onClick={handleSendMessage}>Send</button>
        </div>
    );
};

export default ChatInterface;
