import React from 'react';

const FriendCard = ({ friend, onConnect }) => {
    return (
        <div className="friend-card">
            <img 
                src={friend.profilePicture || 'https://via.placeholder.com/150'} 
                alt={`${friend.name}'s profile`} 
                style={{ width: 100, height: 100, borderRadius: '50%' }}
            />
            <h3>{friend.name}</h3>
            <p>{friend.bio || 'No bio available'}</p>
            <div className="interests">
                {friend.interests && friend.interests.map((interest, index) => (
                    <span key={index} className="interest-tag">{interest}</span>
                ))}
            </div>
            {onConnect && (
                <button onClick={() => onConnect(friend.id)}>Connect</button>
            )}
        </div>
    );
};

export default FriendCard;
