import { io } from 'socket.io-client';

// Create a singleton socket instance
let socket;

const getSocket = () => {
    if (!socket) {
        socket = io(process.env.REACT_APP_SOCKET_URL || 'http://localhost:5000', {
            reconnection: true,
            reconnectionDelay: 1000,
            reconnectionAttempts: 5,
            autoConnect: false
        });
    }
    return socket;
};

const connectSocket = () => {
    const socket = getSocket();
    if (!socket.connected) {
        socket.connect();
        console.log('Socket connected');
    }
};

const disconnectSocket = () => {
    if (socket && socket.connected) {
        socket.disconnect();
        console.log('Socket disconnected');
    }
};

const onMessageReceived = (callback) => {
    socket.on('message', callback);
};

const receiveMessages = (callback) => {
    socket.on('receive_message', callback);
};

const sendMessage = (message) => {
    socket.emit('private_message', message);
};

const markMessagesAsRead = ({ userId, friendId }) => {
    socket.emit('mark_messages_read', { userId, friendId });
};

const onMessagesRead = (callback) => {
    socket.on('messages_read', callback);
};

const onMessagesMarkedRead = (callback) => {
    socket.on('messages_marked_read', callback);
};

const onMessageSent = (callback) => {
    socket.on('message_sent', callback);
};

const onMessageError = (callback) => {
    socket.on('message_error', callback);
};

const sendTypingIndicator = (sender, recipient) => {
    socket.emit('typing', { sender, recipient });
};

const onUserTyping = (callback) => {
    socket.on('user_typing', callback);
};

export { 
    connectSocket, 
    disconnectSocket, 
    onMessageReceived, 
    sendMessage, 
    receiveMessages, 
    markMessagesAsRead, 
    onMessagesRead, 
    onMessagesMarkedRead,
    onMessageSent,
    onMessageError,
    sendTypingIndicator,
    onUserTyping
};
