import React, { useState, useEffect } from 'react';
import { useAuth } from '../../hooks/useAuth';
import { 
    fetchFriends, 
    fetchPotentialFriends, 
    sendFriendRequest, 
    getFriendRequests,
    acceptFriendRequest
} from '../../services/api';
import FriendCard from '../Matching/FriendCard';
import './Dashboard.css';

const Dashboard = () => {
    const { user } = useAuth();
    const [friends, setFriends] = React.useState([]);

    // Fetch friends data from the API
    React.useEffect(() => {
        const fetchFriends = async () => {
            if (user && user.id) {
                try {
                    // This is a placeholder - in a real app, you'd fetch from your API
                    // const response = await fetch(`/api/matching/friends/${user.id}`);
                    // const data = await response.json();
                    // setFriends(data);

                    // Placeholder data for testing
                    setFriends([
                        { id: 1, name: 'Jane Doe', interests: ['Reading', 'Hiking'], profilePicture: 'https://via.placeholder.com/150' },
                        { id: 2, name: 'John Smith', interests: ['Gaming', 'Cooking'], profilePicture: 'https://via.placeholder.com/150' },
                    ]);
                } catch (error) {
                    console.error('Error fetching friends:', error);
                }
            }
        };

        fetchFriends();
    }, [user]);

    if (!user) {
        return <div>Loading...</div>;
    }

    return (
        <div className="dashboard">
            <h1>Welcome, {user.email || 'Friend'}!</h1>
            <h2>Your Friends</h2>
            <div className="friend-list">
                {friends.length > 0 ? friends.map(friend => (
                    <FriendCard key={friend.id} friend={friend} />
                )) : <p>No friends found. Start matching!</p>}
            </div>
        </div>
    );
};

export default Dashboard;
