import React, { useState, useEffect } from 'react';
import { useAuth } from '../../hooks/useAuth';
import { 
    fetchFriends, 
    fetchPotentialFriends, 
    sendFriendRequest, 
    getFriendRequests,
    acceptFriendRequest
} from '../../services/api';
import FriendCard from '../Matching/FriendCard';
import './Dashboard.css';

const Dashboard = () => {
    const { user, loading: authLoading } = useAuth();
    const [friends, setFriends] = useState([]);
    const [potentialFriends, setPotentialFriends] = useState([]);
    const [friendRequests, setFriendRequests] = useState([]);
    const [activeTab, setActiveTab] = useState('friends');
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    // Fetch friends data
    useEffect(() => {
        const loadFriends = async () => {
            if (authLoading || !user) return;

            try {
                setLoading(true);
                const data = await fetchFriends();
                setFriends(data);
            } catch (err) {
                console.error('Error fetching friends:', err);
                setError('Failed to load your friends. Please try again later.');
            } finally {
                setLoading(false);
            }
        };

        loadFriends();
    }, [user, authLoading]);

    // Fetch potential friends
    useEffect(() => {
        const loadPotentialFriends = async () => {
            if (authLoading || !user || activeTab !== 'discover') return;

            try {
                setLoading(true);
                const data = await fetchPotentialFriends();
                setPotentialFriends(data);
            } catch (err) {
                console.error('Error fetching potential friends:', err);
                setError('Failed to load potential friends. Please try again later.');
            } finally {
                setLoading(false);
            }
        };

        loadPotentialFriends();
    }, [activeTab, user, authLoading]);

    // Fetch friend requests
    useEffect(() => {
        const loadFriendRequests = async () => {
            if (authLoading || !user || activeTab !== 'requests') return;

            try {
                setLoading(true);
                const data = await getFriendRequests();
                setFriendRequests(data);
            } catch (err) {
                console.error('Error fetching friend requests:', err);
                setError('Failed to load friend requests. Please try again later.');
            } finally {
                setLoading(false);
            }
        };

        loadFriendRequests();
    }, [activeTab, user, authLoading]);

    if (!user) {
        return <div>Loading...</div>;
    }

    return (
        <div className="dashboard">
            <h1>Welcome, {user.email || 'Friend'}!</h1>
            <h2>Your Friends</h2>
            <div className="friend-list">
                {friends.length > 0 ? friends.map(friend => (
                    <FriendCard key={friend.id} friend={friend} />
                )) : <p>No friends found. Start matching!</p>}
            </div>
        </div>
    );
};

export default Dashboard;
